 */
void
MakeTables(int q, u_char *lqt, u_char *cqt)
{
  int i;
  int factor = q;

  if (q  99) factor = 99;
  if (q  255) lq = 255;
    lqt[i] = lq;

    if (cq  255) cq = 255;
